$size: var(--zen-checkbox-size, 20px);
$border-width: var(--zen-checkbox-border-width, 2px);
$border-color: var(--zen-checkbox-border-color, #cbcbcb);
$border-radius: var(--zen-input-border-radius, 8px);
$checked-color: var(--zen-checkbox-checked-color, #1d1d1d);
$disabled-opacity: var(--zen-checkbox-disabled-opacity, 0.6);
$focus-ring-color: var(--zen-checkbox-focus-ring-color, #999);

